import React from 'react';
import { motion } from 'framer-motion';
import TopPage from '../components/TopPage';
import Footer from '../components/Footer';
import './About.css';

function About() {
  // Honors data (short, crisp content)
  const honors = [
    "Children's Science Foundation Award – Secured 3 STEM patents before age 18",
    "Asian Space Settlement Design Challenge – Gold Medalist in Nationals & Regionals",
    "3rd in World Series of Innovation – Prize: $500",
    "Moonshot Pirates '23 – Top 7 global finalist out of 500+ teams"
  ];

  // Extracurricular activities (ECs) data
  const ecs = [
    "Head Developer & Patentee, CAR.L.A – Developed a vehicle device addressing regulatory loopholes",
    "Freelance 3D Artist – Sold 25+ professional designs, earning over $2,500",
    "Patentee, OT 2'3 Engine – Finalist for Future Port Awards '23 with innovative combustion engine design",
    "Developer & Patentee, Shared Computing Architecture – Pioneered 5G-enabled CPU/memory sharing",
    "HOD, 3D Dept. in Space Settlement Design Challenge – Led a team of 7 designers",
    "Individual Researcher – Explored nanomaterials for self-cleaning automotive surfaces"
  ];

  // Common animation variants for section content
  const sectionAnimation = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <>
      <TopPage />
      <main className="about-main">
        {/* Introduction */}
        <motion.section
          className="about-intro"
          initial="hidden"
          whileInView="visible"
          transition={{ duration: 0.6 }}
          variants={sectionAnimation}
          viewport={{ once: true }}
        >
          <h2>About Me</h2>
          <p>
            I'm Pranav Chahal – a creative innovator with expertise in 3D design, programming, and web development.
            I merge design and technology to bring visionary projects to life.
          </p>
        </motion.section>

        {/* Honors Section */}
        <motion.section
          className="honors"
          initial="hidden"
          whileInView="visible"
          transition={{ duration: 0.6, delay: 0.2 }}
          variants={sectionAnimation}
          viewport={{ once: true }}
        >
          <h3>Honors & Awards</h3>
          <ul>
            {honors.map((item, index) => (
              <motion.li
                key={index}
                initial={{ opacity: 0, x: -20 }}
                whileInView={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                {item}
              </motion.li>
            ))}
          </ul>
        </motion.section>

        {/* ECs Section */}
        <motion.section
          className="ecs"
          initial="hidden"
          whileInView="visible"
          transition={{ duration: 0.6, delay: 0.4 }}
          variants={sectionAnimation}
          viewport={{ once: true }}
        >
          <h3>Extracurriculars</h3>
          <ul>
            {ecs.map((item, index) => (
              <motion.li
                key={index}
                initial={{ opacity: 0, x: 20 }}
                whileInView={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                {item}
              </motion.li>
            ))}
          </ul>
        </motion.section>
      </main>
      <Footer />
    </>
  );
}

export default About;
