import React from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { TorusKnot, OrbitControls } from '@react-three/drei';
import { motion, useViewportScroll, useTransform } from 'framer-motion';

function AnimatedTorusKnot(props) {
  const ref = React.useRef();
  useFrame(() => {
    if(ref.current) {
      // Continuous rotation for the 3D object.
      ref.current.rotation.x += 0.01;
      ref.current.rotation.y += 0.01;
    }
  });
  return (
    <TorusKnot ref={ref} args={[1.5, 0.5, 100, 16]} {...props}>
      <meshStandardMaterial attach="material" color="#ff6347" />
    </TorusKnot>
  );
}

function Hero() {
  // Use Framer Motion scroll hooks.
  const { scrollY } = useViewportScroll();
  const yOffset = useTransform(scrollY, [0, 300], [0, -50]);
  const fadeOut = useTransform(scrollY, [0, 300], [1, 0]);

  return (
    <section className="hero" style={{ position: 'relative', height: '100vh', overflow: 'hidden' }}>
      {/* Canvas for 3D background */}
      <Canvas style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}>
        <ambientLight intensity={0.5} />
        <directionalLight position={[10, 10, 5]} intensity={1} />
        <AnimatedTorusKnot position={[0, 0, 0]} />
        <OrbitControls enableZoom={false} enablePan={false} />
      </Canvas>

      {/* Optional background overlay for text readability */}
      <div style={{
          position: 'absolute', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%', 
          background: 'rgba(0,0,0,0.3)' 
      }} />

      {/* Centered hero content with scroll animations */}
      <motion.div
        className="hero-content"
        style={{ 
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          textAlign: 'center',
          color: '#fff',
          y: yOffset,
          opacity: fadeOut
        }}
        initial={{ scale: 0.9 }}
        animate={{ scale: 1 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
      >
        <h2 style={{ fontSize: '3rem', margin: 0 }}>Digital Art Collection</h2>
        <p style={{ fontSize: '1.5rem', marginTop: '10px' }}>by: Pranav Chahal</p>
        <a href="#gallery" className="btn" style={{
            display: 'inline-block',
            marginTop: '20px',
            padding: '10px 20px',
            backgroundColor: '#fff',
            color: '#333',
            fontWeight: 'bold',
            textDecoration: 'none',
            transition: 'transform 0.3s ease'
        }}>
          Explore Gallery
        </a>
      </motion.div>
    </section>
  );
}

export default Hero;
