import React from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { TorusKnot, OrbitControls } from '@react-three/drei';
import { motion, useViewportScroll, useTransform } from 'framer-motion';

function AnimatedTorusKnot(props) {
  const ref = React.useRef();
  useFrame(() => {
    // Rotates the torus knot continuously.
    if (ref.current) {
      ref.current.rotation.x += 0.01;
      ref.current.rotation.y += 0.01;
    }
  });
  return (
    <TorusKnot ref={ref} args={[1.5, 0.5, 100, 16]} {...props}>
      <meshStandardMaterial attach="material" color="#ff6347" />
    </TorusKnot>
  );
}

function Hero() {
  // Use Framer Motion hooks to drive animations based on scroll position.
  const { scrollY } = useViewportScroll();
  // As the user scrolls, the text moves upward (y offset) and fades out.
  const yOffset = useTransform(scrollY, [0, 300], [0, -50]);
  const fadeOut = useTransform(scrollY, [0, 300], [1, 0]);

  return (
    <section className="hero">
      {/* React-three-fiber Canvas with an animated torus knot */}
      <Canvas style={{ position: 'absolute', top: 0, height: '100vh', width: '100%' }}>
        <ambientLight intensity={0.5} />
        <directionalLight position={[10, 10, 5]} intensity={1} />
        <AnimatedTorusKnot position={[0, 0, 0]} />
        <OrbitControls enableZoom={false} enablePan={false} />
      </Canvas>
      {/* The hero text is wrapped in a Framer Motion component for scroll animation */}
      <motion.div className="hero-content" style={{ y: yOffset, opacity: fadeOut }}>
        <h2>Digital Art Collection</h2>
        <p>by: Pranav Chahal</p>
        <a href="#gallery" className="btn">Explore Gallery</a>
      </motion.div>
    </section>
  );
}

export default Hero;
